package javaPrograms1;

public class Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=23;
    if(n/2==0)
    {
    	System.out.println("given number is even:"+n);
    }
    else
    {
    	System.out.println("given number is odd:" +n);
    }
	
		
	}

}
